void submit_password(lv_event_t * e)
{
	if (strcmp(lv_textarea_get_text(ui_TextArea2),"123")==0)
    {
        lv_label_set_text(ui_Label2,"密码正确");
        _ui_screen_change(ui_adminScreen, LV_SCR_LOAD_ANIM_MOVE_LEFT, 500, 500);
        
    }else{
        lv_label_set_text(ui_Label2,"密码错误");
    }
    
}

void userScreen_init(lv_event_t * e)
{
	lv_group_remove_all_objs(lv_group_get_default());
    lv_group_add_obj(lv_group_get_default(),ui_enterpassword);
    lv_group_add_obj(lv_group_get_default(),ui_camera);

}

void click_camera_open(lv_event_t * e)
{
	disp_disable_update();
    ov_display_enable();
    _ui_screen_change(ui_whiteScreen, LV_SCR_LOAD_ANIM_OVER_BOTTOM, 500, 0);
}

void click_camera_close(lv_event_t * e)
{
    ov_display_disable();
	disp_enable_update();
    _ui_screen_change(ui_userScreen, LV_SCR_LOAD_ANIM_OVER_BOTTOM, 500, 200 );
}

